package com.chainsys.demo4;

public class Equation {
    
	public static void main(String[] args) {
		// TODO Auto-generated method stub
  	
     int eqn;
     for(int y=5;y<=10;y++) 
     {
       eqn=2*(y*y)+y+5;
       System.out.println("2*"+"("+y+"*"+y+")+"+y+"+"+5+"="+eqn);
	 }
  
	}
}
